@font-face {
	font-family: 'Noto Serif SC';
	font-style: normal;
	font-weight: 400;
	src: local('Noto Serif SC'), local('NotoSerifSC-Regular'), url('fonts/noto-serif-sc/NotoSerifSC-Regular.woff2') format('woff2');
	font-display: swap;
}

@font-face {
	font-family: 'Noto Serif SC';
	font-style: normal;
	font-weight: 700;
	src: local('Noto Serif SC Bold'), local('NotoSerifSC-Bold'), url('fonts/noto-serif-sc/NotoSerifSC-Bold.woff2') format('woff2');
	font-display: swap;
}

/* ==================== åŸºç¡€æ ·å¼ ==================== */
* {
	font-family: "Noto Serif SC", "Helvetica", "PingFang SC", "Microsoft YaHei", sans-serif;
	box-sizing: border-box;
}

html,
body {
	background-image: url(img/th1.webp);
	background-size: cover;
	background-position: center;
	background-repeat: no-repeat;
	background-attachment: fixed;
	margin: 0;
	padding: 0;
	display: flex;
	flex-direction: column;
	align-items: center;
	/* ç¡®ä¿è¿™ä¸ªå­˜åœ¨ */
	width: 100%;
	min-height: 100vh;
	overflow-x: hidden;
	font-family: "Noto Serif SC", "Helvetica", "PingFang SC", "Microsoft YaHei", sans-serif;
}

/* ä¸»é¡µç¦æ­¢æ»šåŠ¨ */
body.home-page {
	overflow: hidden;
	height: 100vh;
}

/* ç¡®ä¿ä¸»é¡µå®¹å™¨æ­£å¸¸æ˜¾ç¤º */
body.home-page .container {
	overflow: visible;
}

/* ==================== å¯¼èˆªæ æ ·å¼ ==================== */
.navbar {
	display: flex;
	align-items: center;
	justify-content: space-between;
	background-color: rgba(0, 0, 0, 0.3);
	backdrop-filter: blur(15px);
	-webkit-backdrop-filter: blur(15px);
	padding: 12px 30px;
	position: fixed;
	top: 0;
	width: 100%;
	z-index: 1000;
	border-bottom: none;
}

.navbar-left {
	display: flex;
	align-items: center;
	gap: 15px;
}

.logo {
	height: 45px;
	width: 45px;
	border-radius: 50%;
	object-fit: cover;
	transition: transform 0.3s ease;
}

.logo:hover {
	transform: scale(1.05);
}

.site-name {
	font-size: 22px;
	color: white;
	font-weight: bold;
	letter-spacing: 0.5px;
	text-shadow: 0 2px 8px rgba(0, 0, 0, 0.5);
}

.navbar-menu {
	list-style: none;
	margin: 0;
	padding: 0;
	display: flex;
	gap: 25px;
	margin-left: auto;
}

.navbar-menu li a {
	color: white;
	text-decoration: none;
	font-weight: 500;
	font-size: 16px;
	text-shadow: 0 1px 3px rgba(0, 0, 0, 0.6);
	transition: all 0.3s ease;
	padding: 8px 12px;
	border-radius: 6px;
}

.navbar-menu li a:hover {
	color: #a8d8ea;
	background: rgba(168, 216, 234, 0.1);
}

.navbar-menu li a.active {
	color: #4dd0e1;
	background: rgba(77, 208, 225, 0.15);
}

.nav-toggle {
	display: none;
	font-size: 1.5rem;
	cursor: pointer;
	color: white;
	padding: 8px;
	border-radius: 4px;
	transition: background 0.3s ease;
}

.nav-toggle:hover {
	background: rgba(255, 255, 255, 0.1);
}

/* ==================== ä¸»å®¹å™¨æ ·å¼ ==================== */
.container {
	display: flex;
	flex-direction: column;
	align-items: center;
	/* æ°´å¹³å±…ä¸­ */
	justify-content: center;
	/* å‚ç›´å±…ä¸­ */
	text-align: center;
	gap: 35px;
	padding: 40px 30px;
	max-width: 800px;
	margin-top: 80px;
	flex: 1;
	min-height: calc(100vh - 160px);
	width: 100%;
	/* ç¡®ä¿å®½åº¦è®¾ç½® */
}

.avatar {
	width: 160px;
	height: 160px;
	border-radius: 50%;
	object-fit: cover;
	box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3), 0 0 0 4px rgba(255, 255, 255, 0.1);
	transition: all 0.4s ease;
	margin-top: 10px;
}

.avatar:hover {
	transform: scale(1.08);
	box-shadow: 0 12px 40px rgba(0, 0, 0, 0.4), 0 0 0 4px rgba(77, 208, 225, 0.3);
}

.motto {
	font-size: 2.4rem;
	font-weight: 700;
	color: rgb(255, 255, 255);
	margin: 10px 0;
	text-shadow: 0 4px 15px rgba(0, 0, 0, 0.6);
	line-height: 1.3;
	background: linear-gradient(135deg, #ffffff 0%, #a8d8ea 100%);
	-webkit-background-clip: text;
	-webkit-text-fill-color: transparent;
	background-clip: text;
}

.description {
	font-size: 1.4rem;
	color: rgba(255, 255, 255, 0.95);
	font-weight: 400;
	line-height: 1.8;
	max-width: 680px;
	text-align: justify;
	white-space: normal;
	font-style: italic;
	text-indent: 2em;
	margin: 10px 0;
	text-shadow: 0 2px 8px rgba(0, 0, 0, 0.5);
	padding: 20px;
	background: rgba(255, 255, 255, 0.05);
	backdrop-filter: blur(5px);
	border-radius: 12px;
	border: 1px solid rgba(255, 255, 255, 0.1);
}

.tags {
	display: flex;
	justify-content: center;
	flex-wrap: wrap;
	gap: 12px;
	margin-top: 25px;
}

.tag {
	background: linear-gradient(135deg, #4dd0e1 0%, #26c6da 100%);
	color: #fff;
	padding: 12px 24px;
	border-radius: 25px;
	cursor: pointer;
	transition: all 0.3s ease;
	font-size: 1rem;
	font-weight: 500;
	box-shadow: 0 4px 15px rgba(77, 208, 225, 0.3);
	border: none;
	position: relative;
	overflow: hidden;
}

.tag::before {
	content: '';
	position: absolute;
	top: 0;
	left: -100%;
	width: 100%;
	height: 100%;
	background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
	transition: left 0.5s;
}

.tag:hover {
	transform: translateY(-3px);
	box-shadow: 0 8px 25px rgba(77, 208, 225, 0.5);
}

.tag:hover::before {
	left: 100%;
}

/* ==================== é€šç”¨é¡µé¢æ ·å¼ ==================== */
.page-container {
	position: relative;
	padding: 120px 20px 40px;
	max-width: 1200px;
	margin: 0 auto;
	width: 100%;
	flex: 1;
	min-height: auto;
}

.page-header {
	text-align: center;
	margin-bottom: 50px;
	color: white;
}

.page-header h1 {
	font-size: 2.8rem;
	margin-bottom: 15px;
	text-shadow: 0 4px 15px rgba(0, 0, 0, 0.6);
	background: linear-gradient(135deg, #ffffff 0%, var(--theme-color) 100%);
	-webkit-background-clip: text;
	background-clip: text;
}

.page-header p {
	font-size: 1.2rem;
	color: rgba(255, 255, 255, 0.9);
	font-style: italic;
}

.section-title {
	color: var(--theme-color);
	border-bottom: 2px solid rgba(var(--theme-color-rgb), 0.3);
	padding-bottom: 10px;
	margin-bottom: 30px;
	font-size: 1.8rem;
	text-shadow: 0 2px 8px rgba(0, 0, 0, 0.5);
}

.card-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
	gap: 30px;
	margin-top: 30px;
	justify-items: center;
	max-width: 100%;
}

.base-card {
	background: rgba(255, 255, 255, 0.08);
	backdrop-filter: blur(15px);
	border-radius: 20px;
	padding: 0;
	overflow: hidden;
	transition: all 0.3s ease;
	box-shadow: 0 8px 30px rgba(0, 0, 0, 0.3);
	cursor: pointer;
	border: 1px solid rgba(255, 255, 255, 0.1);
	width: 100%;
	max-width: 350px;
}

.base-card:hover {
	transform: translateY(-8px);
	background: rgba(255, 255, 255, 0.12);
	box-shadow: 0 15px 40px rgba(0, 0, 0, 0.4);
}

.card-image {
	width: 100%;
	height: 200px;
	object-fit: cover;
	display: block;
}

.card-content {
	padding: 25px;
}

.card-content h3 {
	margin-top: 0;
	color: var(--theme-color);
	font-size: 1.4rem;
	margin-bottom: 12px;
}

.card-content p {
	color: rgba(255, 255, 255, 0.9);
	line-height: 1.6;
	margin-bottom: 15px;
}

.card-content small {
	color: rgba(255, 255, 255, 0.7);
	font-size: 0.9rem;
}

/* ==================== é¡µè„šæ ·å¼ ==================== */
.page-footer,
.blog-footer {
	width: 100%;
	text-align: center;
	padding: 25px;
	color: rgba(255, 255, 255, 0.7);
	border-top: 1px solid rgba(255, 255, 255, 0.15);
	background: rgba(0, 0, 0, 0.4);
	backdrop-filter: blur(15px);
	margin-top: auto;
	font-size: 0.9rem;
	line-height: 1.4;
	flex-shrink: 0;
}

.blog-footer p {
	margin: 5px 0;
}

/* ==================== ç§»åŠ¨ç«¯é€‚é… ==================== */
/* å¹³æ¿è®¾å¤‡ (768pxåŠä»¥ä¸‹) */
@media (max-width: 768px) {
	body {
		font-size: 16px;
	}

	body.home-page {
		overflow: hidden;
		height: 100vh;
	}

	.navbar {
		padding: 10px 15px;
	}

	.site-name {
		font-size: 1rem;
	}

	.navbar-menu {
		display: none;
	}

	.nav-toggle {
		display: block;
	}

	.navbar-menu.active {
		display: flex;
		flex-direction: column;
		position: absolute;
		top: 100%;
		right: 10px;
		background-color: rgba(0, 0, 0, 0.9);
		backdrop-filter: blur(20px);
		-webkit-backdrop-filter: blur(20px);
		border-radius: 12px;
		padding: 12px 0;
		gap: 0;
		width: auto;
		min-width: 140px;
		box-shadow: 0 8px 25px rgba(0, 0, 0, 0.4);
		border: 1px solid rgba(255, 255, 255, 0.15);
		z-index: 1001;
	}

	.navbar-menu.active li {
		margin: 0;
	}

	.navbar-menu.active li a {
		color: #ffffff;
		display: block;
		padding: 12px 20px;
		text-align: left;
		font-size: 0.95rem;
		text-shadow: 0 1px 2px rgba(0, 0, 0, 0.8);
		transition: all 0.3s;
		border-radius: 0;
	}

	.navbar-menu.active li a:hover {
		background-color: rgba(168, 216, 234, 0.2);
		color: #a8d8ea;
	}

	.container {
		padding: 100px 20px 20px;
		max-width: 95%;
		gap: 25px;
		margin-top: 60px;
		min-height: calc(100vh - 140px);
	}

	.avatar {
		width: 120px;
		height: 120px;
		margin-top: 0;
	}

	.motto {
		font-size: 1.8rem !important;
		margin: 5px 0;
	}

	.description {
		font-size: 1.1rem !important;
		line-height: 1.6;
		text-indent: 1.5em;
		padding: 15px;
		margin: 5px 0;
	}

	.tag {
		padding: 10px 18px;
		font-size: 0.9rem !important;
	}

	.tags {
		gap: 10px;
		margin-top: 20px;
	}

	.blog-footer {
		padding: 15px;
		font-size: 0.8rem;
	}

	.page-container {
		padding: 100px 15px 30px;
	}

	.page-header h1 {
		font-size: 2.2rem;
	}

	.card-grid {
		grid-template-columns: 1fr;
		gap: 25px;
	}

	.base-card {
		max-width: 100%;
		margin: 0 10px;
	}

	.card-content {
		padding: 20px 15px;
	}
}

/* æ‰‹æœºè®¾å¤‡ (480pxåŠä»¥ä¸‹) */
@media (max-width: 480px) {
	.site-name {
		font-size: 0.9rem;
	}

	.navbar-menu.active {
		right: 8px;
		min-width: 120px;
		max-width: 140px;
		padding: 10px 0;
	}

	.navbar-menu.active li a {
		padding: 10px 15px;
		font-size: 0.85rem;
	}

	.container {
		padding: 80px 15px 15px;
		gap: 20px;
		margin-top: 50px;
		min-height: calc(100vh - 120px);
	}

	.avatar {
		width: 100px;
		height: 100px;
	}

	.motto {
		font-size: 1.6rem !important;
	}

	.description {
		font-size: 1rem !important;
		text-indent: 1em;
		padding: 12px;
	}

	.tag {
		padding: 8px 16px;
		font-size: 0.85rem !important;
	}

	.tags {
		gap: 8px;
	}

	.page-container {
		padding: 80px 10px 20px;
	}

	.page-header h1 {
		font-size: 1.8rem;
		line-height: 1.2;
		margin-bottom: 10px;
	}

	.page-header p {
		font-size: 1rem;
	}

	.section-title {
		font-size: 1.5rem;
		padding-bottom: 8px;
		margin-bottom: 20px;
	}

	.base-card {
		padding: 15px;
		margin: 0;
		width: 100%;
	}

	.card-content {
		padding: 12px 10px;
	}

	.card-content h3 {
		font-size: 1.1rem;
		margin-bottom: 8px;
	}

	.card-content p {
		font-size: 0.9rem;
		line-height: 1.4;
	}

	.card-image {
		height: 150px;
	}

	.page-footer {
		padding: 20px 15px;
		font-size: 0.8rem;
	}
}

/* è¶…å°å±å¹•è®¾å¤‡ (360pxåŠä»¥ä¸‹) */
@media (max-width: 360px) {
	.container {
		padding: 70px 8px 15px;
	}

	.avatar {
		width: 90px;
		height: 90px;
	}

	.motto {
		font-size: 1.4rem !important;
	}

	.description {
		font-size: 0.9rem !important;
		padding: 10px;
	}

	.tag {
		padding: 6px 12px;
		font-size: 0.8rem !important;
	}

	.page-container {
		padding: 70px 8px 15px;
	}

	.page-header h1 {
		font-size: 1.6rem;
	}

	.section-title {
		font-size: 1.3rem;
	}

	.base-card {
		padding: 12px;
	}

	.card-content {
		padding: 10px 8px;
	}

	.card-image {
		height: 130px;
	}
}

