}

// ================================================================= 
// 3. API é›†æˆï¼šISS ä½ç½® (wheretheiss.at API) 
// ================================================================= 
async function fetchISSLocation() { 
    const url = 'https://api.wheretheiss.at/v1/satellites/25544'; 
    const issElement = document.getElementById('issLocation'); 
    
    try { 
        const response = await fetch(url); 
        if (!response.ok) throw new Error('Network response was not ok'); 
        
        const data = await response.json(); 
        const { latitude, longitude } = data; 
        
        issElement.innerHTML = ` 
            <h3>å›½é™…ç©ºé—´ç«™ (ISS) å®æ—¶ä½ç½®</h3> 
            <p>çº¬åº¦: <strong>${parseFloat(latitude).toFixed(4)}Â°</strong></p> 
            <p>ç»åº¦: <strong>${parseFloat(longitude).toFixed(4)}Â°</strong></p> 
        `; 
    } catch (error) { 
        issElement.innerHTML = ` 
            <h3>ISS å®æ—¶ä½ç½®</h3> 
            <p style="color: #ff5252;">æ•°æ®åŠ è½½å¤±è´¥ï¼Œè¯·ç¨åé‡è¯•ã€‚</p> 
        `; 
        console.error("Error fetching ISS data:", error); 
