}

// ================================================================= 
// 4. API é›†æˆï¼šæœˆç›¸æ•°æ® (æœ¬åœ°æ¨¡æ‹Ÿæ•°æ®) 
// ================================================================= 
function mockMoonPhase() {
    const phases = ["æ–°æœˆ", "å¨¥çœ‰æœˆ", "ä¸Šå¼¦æœˆ", "ç›ˆå‡¸æœˆ", "æ»¡æœˆ", "äºå‡¸æœˆ", "ä¸‹å¼¦æœˆ", "æ®‹æœˆ"]; 
    const phaseName = phases[Math.floor(Math.random() * phases.length)]; 
    const illumination = Math.floor(Math.random() * 100) + "%"; 
    
    const moonElement = document.getElementById('moonPhase'); 
    
    moonElement.innerHTML = ` 
        <h3>æœˆç›¸å®æ—¶æ•°æ®</h3> 
        <p>å½“å‰æœˆç›¸: <strong>${phaseName}</strong></p> 
        <p>æœˆçƒç…§åº¦: <strong>${illumination}</strong></p> 
    `;
}

// ================================================================= 
// 5. é¡µé¢åˆå§‹åŒ–åŠäº¤äº’é€»è¾‘
// =================================================================
function initAstronomyPage() {
    // åŠ¨æ€æ¸²æŸ“å¤©æ–‡çŸ¥è¯†å¡ç‰‡
    generateAstroCards();
    
    // åŠ è½½å®æ—¶ API æ•°æ®
