 * @param {string} name - å‚æ•°åç§° ('file')
 * @returns {string | null} - å‚æ•°å€¼æˆ– null
 */
function getQueryParam(name) {
    const urlParams = new URLSearchParams(window.location.search);
    return urlParams.get(name);
}

/**
 * æ ¸å¿ƒæ¸²æŸ“å‡½æ•°ï¼šåŠ è½½ Markdown æ–‡ä»¶ï¼Œè§£æ Front Matterï¼Œå¹¶æ¸²æŸ“å†…å®¹
 */
async function renderPost() {
    // URL å‚æ•°åå·²ç»Ÿä¸€ä¸º 'file'
    const file = getQueryParam('file');
    // ä½¿ç”¨ HTML ä¸­å®šä¹‰çš„ ID '#article-content'
    const container = document.getElementById('article-content');

    if (!container) {
        console.error("æ‰¾ä¸åˆ°æ–‡ç« å†…å®¹å®¹å™¨ #article-content");
        return;
    }

    // æ£€æŸ¥å…¨å±€ä¾èµ– (å¿…é¡»é€šè¿‡ <script> æ ‡ç­¾åŠ è½½)
    // å‡è®¾ä½ å·²åœ¨ HTML ä¸­åŠ è½½ marked, jsyaml å’Œ DOMPurify
    if (typeof marked === 'undefined' || typeof jsyaml === 'undefined' || typeof DOMPurify === 'undefined') {
        container.innerHTML = '<p class="error-message">åˆå§‹åŒ–å¤±è´¥ï¼šè¯·ç¡®ä¿ marked.js, jsyaml.js, å’Œ DOMPurify å·²åŠ è½½ã€‚</p>';
        return;
    }

    if (!file) {
        container.innerHTML = '<div class="error-message"><h3>æ–‡ç« å‚æ•°ç¼ºå¤±</h3><p>æœªæ‰¾åˆ°æŒ‡å®šæ–‡ç« æ–‡ä»¶ã€‚</p><a href="blog.html" class="back-link">â† è¿”å›åšå®¢åˆ—è¡¨</a></div>';
        return;
    }

    // æ˜¾ç¤ºåŠ è½½çŠ¶æ€ (ä½¿ç”¨ CSS ä¸­å®šä¹‰çš„ .loading ç±»)
    container.innerHTML = '<div class="loading">æ­£åœ¨åŠ è½½æ–‡ç« ...</div>';

    try {
        // å°è¯•åŠ è½½æ–‡ä»¶
        const res = await fetch(`./posts/${file}`); 
        
        if (!res.ok) {
            throw new Error(`æ–‡ç« æœªæ‰¾åˆ° (HTTP ${res.status})`);
        }
        
        const text = await res.text();
        
        // 1. æå– YAML Front Matter
        const yamlMatch = text.match(/^---\n([\s\S]+?)\n---/);
        let metadata = {};
        let content = text;

        if (yamlMatch) {
            try {
                metadata = jsyaml.load(yamlMatch[1]) || {};
                content = text.replace(yamlMatch[0], '').trim();
            } catch (e) {
                console.warn('YAML Front Matter è§£æå¤±è´¥ï¼Œå°†å¿½ç•¥å…ƒæ•°æ®:', e);
                content = text.replace(/^---\n[\s\S]+?\n---/, '').trim();
            }
        }
        
        // 2. è§£æ Markdown å¹¶è¿›è¡Œ HTML å‡€åŒ– (å®‰å…¨æ€§)
        const htmlContent = marked.parse(content);
        const safeHtml = DOMPurify.sanitize(htmlContent);

        // 3. æ¸²æŸ“æœ€ç»ˆ HTML (åŒ¹é… post.css çš„ç»“æ„)
        container.innerHTML = `
            <header class="article-header">
                <h1 class="article-title">${metadata.title || 'æ— æ ‡é¢˜'}</h1>
                <div class="article-meta">
                    <span class="meta-item"><i>ğŸ—“ï¸</i> ${metadata.date || 'æœªçŸ¥æ—¥æœŸ'}</span> 
                    <span class="meta-item"><i>ğŸ·ï¸</i> ${metadata.category || 'æœªåˆ†ç±»'}</span>
                    ${metadata.author ? `<span class="meta-item"><i>ğŸ‘¤</i> ${metadata.author}</span>` : ''}
                </div>
            </header>
            <div class="article-content post-body">
                ${safeHtml}
            </div>
        `;

        // 4. (å¯é€‰) ä»£ç é«˜äº®
        if (typeof hljs !== 'undefined') {
            document.querySelectorAll('#article-content pre code').forEach((block) => {
                // ä½¿ç”¨ highlight.js è¿›è¡Œé«˜äº®
                hljs.highlightElement(block);
            });
        }
        
        // 5. æ›´æ–°æµè§ˆå™¨æ ‡é¢˜
        document.title = `${metadata.title || 'æ–‡ç« '} | æ˜Ÿç©ºç¬”è®°`;

    } catch (err) {
        console.error('åŠ è½½æ–‡ç« å¤±è´¥', err);
        // æ˜¾ç¤ºé”™è¯¯ä¿¡æ¯ (ä½¿ç”¨ CSS ä¸­å®šä¹‰çš„ .error-message ç±»)
        container.innerHTML = `
            <div class="error-message">
                <h3 class="error-title">åŠ è½½å¤±è´¥</h3>
                <p>æ— æ³•åŠ è½½æ–‡ä»¶ <code>posts/${file}</code>ã€‚</p>
                <p>é”™è¯¯ä¿¡æ¯: ${err.message}</p>
                <a href="blog.html" class="back-link">â† è¿”å›åšå®¢åˆ—è¡¨</a>
            </div>
        `;
    }
}

// é¡µé¢åŠ è½½å®Œæˆåå¼€å§‹æ¸²æŸ“
document.addEventListener('DOMContentLoaded', renderPost);

