    border: 1px solid rgba(255, 255, 255, 0.1);
}

.meta-item i {
    color: #4dd0e1;
    font-style: normal; /* ç¡®ä¿å›¾æ ‡å­—ç¬¦ä¸ä¼šå€¾æ–œ */
}

/* Markdown å†…å®¹æ ·å¼ï¼ˆåº”ç”¨åˆ° JS æ¸²æŸ“çš„ .post-body å†…éƒ¨ï¼‰ */
.article-content .post-body p {
    margin-bottom: 1.5em;
    text-align: justify;
}

.article-content .post-body h2 {
    color: #4dd0e1;
    margin: 2em 0 1em 0;
    font-size: 1.8rem;
    border-bottom: 1px solid rgba(77, 208, 225, 0.2);
    padding-bottom: 10px;
}

.article-content .post-body h3 {
    color: #a8d8ea;
    margin: 1.8em 0 1em 0;
    font-size: 1.4rem;
}

.article-content .post-body blockquote {
    border-left: 4px solid #4dd0e1;
    padding-left: 20px;
    margin: 2em 0;
    font-style: italic;
    color: rgba(255, 255, 255, 0.8);
    background: rgba(77, 208, 225, 0.05);
    padding: 20px;
    border-radius: 0 8px 8px 0;
}

.article-content .post-body code {
    background: rgba(0, 0, 0, 0.3);
    padding: 2px 6px;
    border-radius: 4px;
    font-family: 'Courier New', monospace;
    color: #a8d8ea;
}

.article-content .post-body pre {
    background: rgba(0, 0, 0, 0.3);
    padding: 20px;
    border-radius: 8px;
    overflow-x: auto;
    margin: 2em 0;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.article-content .post-body ul,
.article-content .post-body ol {
    margin: 1.5em 0;
    padding-left: 1.5em;
}

.article-content .post-body li {
    margin-bottom: 0.5em;
}

/* æ–‡ç« æ“ä½œåŒºï¼ˆåœ¨ HTML ä¸­ï¼Œä¸æ–‡ç« å†…å®¹å®¹å™¨ article-content åŒçº§ï¼‰ */
.article-footer {
    margin-top: 50px;
    padding-top: 25px;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    text-align: center;
    color: rgba(255, 255, 255, 0.7);
}

.article-actions {
    display: flex;
    justify-content: center;
    gap: 15px;
    margin-top: 20px;
}

.action-btn {
    display: inline-flex;
    align-items: center;
    gap: 8px;
    padding: 10px 20px;
    background: rgba(255, 255, 255, 0.1);
    color: white;
    text-decoration: none;
    border-radius: 8px;
    transition: all 0.3s ease;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.action-btn:hover {
    background: rgba(77, 208, 225, 0.2);
    transform: translateY(-3px);
}

/* å“åº”å¼è°ƒæ•´ */
@media (max-width: 768px) {
    .article-content {
        padding: 20px;
    }

    .article-title {
        font-size: 2rem;
    }

    .article-meta {
        flex-direction: column;
        align-items: center;
        gap: 10px;
    }

    .article-actions {
        flex-direction: column;
        align-items: center;
    }
}
